import React, { useState } from "react";
import { useStateValue } from "../Context/Context";
import SubTotal from "../subTotal/SubTotal";
import "./Checkout.css";
import { Button } from "style-components";
import {Link} from "react-router-dom";
// import {useNavigate} from "react-router-dom";



function Checkout() {
  const [{ cart, quantity }, dispatch] = useStateValue();
  const [name, setName] = useState("");
  const [shippingAddress, setShippingAddress] = useState("");
  const [billingAddress, setBillingAddress] = useState("");
  const [creditCardInfo, setCreditCardInfo] = useState("");
  // const navigate = useNavigate();

  let total = 0;
  if (cart.length > 0) {
    total = cart.reduce(
      (total, item) => total + item.product.price * item.quantity,
      0
    );
  }

  const handleClear = () => {
    setName("");
    setShippingAddress("");
    setBillingAddress("");
    setCreditCardInfo("");
  };

  const handleSubmit = () => {
    if (
      name == "" ||
      shippingAddress == "" ||
      billingAddress == "" ||
      creditCardInfo == ""
    ) {
      alert("Please enter all inputs");
    } else {
      alert("Your form is submitted successfully!");
      RemoveCart();
      // navigate("/products");
    }
  };
  console.log("cart", quantity);
  const removeFromCart = (id) => {
    dispatch({
      type: "REMOVE_FROM_CART",
      payload: id,
    });
  };
const handleQuantity = (quantity) => {
  dispatch({
    type: "ADD_QUANTITY",
   payload: quantity,
  })
}
  return (
  <div>
  <div className="checkout">
      {cart.length ? (
        <>
      <div className="checkout__left">
        <div>
          {cart.map((item) => (
            <div className="checkoutProduct">
              <img className="checkoutProduct__image" src={item.image} />

              <div className="checkoutProduct__info">
                <p className="checkoutProduct__title">{item.title}</p>
                <p className="checkoutProduct__price">
                  <small>$</small>
                  <strong>{item.price}</strong>
                </p>

                <div className="quantity">
                  <label htmlFor="qty">Qty</label>
                  <input
                    min="1"
                    type="number"
                    id="qty"
                    name="qty"
                    // value={}
                    onChange={(e) => {
                    handleQuantity(e.target.value);
                   
                  }
                  }
                
                  ></input>
                  
                </div>
                <div className="checkoutProduct__button"> 
                <button onClick={() => removeFromCart(item.id)}>
                  Remove from Cart
                </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div className="checkout__right">
        <SubTotal />
          </div>
        </>
      ):(
        <h1> Your cart is Empty</h1 >
        )}
    </div>
    <div className="row">
        <h4 className="text-center">Checkout Form</h4>
        <form noValidate autoComplete="off">
          <div className="text-center p-2">
          <div
              required
              id="standard-required"
              label="Name"
              defaultValue=""
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="text-center">
          <div
              required
              id="standard-required"
              label="Shipping Address"
              defaultValue=""
              value={shippingAddress}
              onChange={(e) => setShippingAddress(e.target.value)}
            />
          </div>
          <div className="text-center">
          <div
              required
              id="standard-required"
              label="Billing Address"
              defaultValue=""
              value={billingAddress}
              onChange={(e) => setBillingAddress(e.target.value)}
            />
          </div>
          <div className="text-center">
          <div
              required
              id="standard-required"
              label="Credit Card Info"
              defaultValue=""
              value={creditCardInfo}
              onChange={(e) => setCreditCardInfo(e.target.value)}
            />
          </div>
          <div className="text-center p-4">
            <Button variant="contained" color="primary" onClick={handleSubmit}>
              Submit
            </Button>
            <Button variant="contained" color="secondary" onClick={handleClear}>
              Clear
            </Button>
          </div>
        </form>
      </div>
      </div>
)
      }
export default Checkout;
