import { useState, useEffect } from "react";
import "./Form.css";
import {Link} from "react-router-dom"

function Form() {
  const initialValues = [Form];
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

Form (<Link to="/cart"> Form </Link>)
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setFormErrors(validate(formValues));
    setIsSubmit(true);
  };

  useEffect(() => {
    console.log(formErrors);
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      console.log(formValues);
    }
  }, [formErrors]);
  const validate = (values) => {
    const errors = {};
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]{2,}$/i;
    if (!values.Name) {
      errors.Name = "Name is required!";
    }
    if (!values.ShippingAdress) {

      errors.ShippingAdress = "Shipping Adress is required!";

    } else if (!regex.test(values.ShippingAdress)) {

      errors.ShippingAdress = "This is not a valid Shipping Adress format!";
    }
    if (!values.CreditCard) {

      errors.CreditCard = "CreditCard is required";

    } else if (values.CreditCard.length < 4) {
      errors.CreditCard = "CreditCard must be more than 4 characters";
    } else if (values.CreditCard.length > 10) {
      errors.CreditCard = "CreditCard cannot exceed more than 10 characters";
    }
    return errors;
  };

  return (
    <div className="container">
      {Object.keys(formErrors).length === 0 && isSubmit ? (
        <div className="ui message success">Signed in successfully</div>
      ) : (
        <pre>{JSON.stringify(formValues, undefined, 2)}</pre>
      )}

      <form onSubmit={handleSubmit}>
        <h1>Login Form</h1>
        <div className="ui divider"></div>
        <div className="ui form">
          <div className="field">
            <label>Name</label>
            <input
              type="text"
              name="Name"
              placeholder="Name"
              value={formValues.Name}
              onChange={handleChange}
            />
          </div>
          <p>{formErrors.Name}</p>
          <div className="field">
            <label>Shipping Address</label>
            <input
              type="text"
              name="Shipping Adress"
              placeholder="Shipping Adress"
              value={formValues.ShippingAdress}
              onChange={handleChange}
            />
          </div>
          <p>{formErrors.ShippingAdress}</p>
          <div className="field">
            <label>Credit Card </label>
            <input
              type="CreditCard"
              name="CreditCard"
              placeholder="CreditCard"
              value={formValues.CreditCard}
              onChange={handleChange}
            />
          </div>
          <p>{formErrors.CreditCard}</p>
          <button className="fluid ui button blue">Submit</button>
        </div>
      </form>
    </div>
  );
}

export default Form;
